    private void Acessar(){
        if (jTextField1Usuario.getText().equals("admin") //se campo usuario e senha receber admin admin fa√ßa
        &&jPasswordField1Senha.getText().equals("admin")){
        Menul tela = new Menul();//objeto menul
        tela.setVisible(true);//tornar Menul visivel e fechar
        dispose();
        } else {
        JOptionPane.showMessageDialog(rootPane, "Senha ou Usuario Invalidos!"); }
    }

